mapper.select:
select sc.*, sj.id subjectId, sj.code subjectCode, sj.year subjectYear, sj.semester subjectSemester, sj.name subjectName, sj.score subjectScore, sj.division subjectDivision, sd.id detailId, sd.title, sd.subtitle, a.id adminId, a.name adminName from score sc left join subject sj on sc.subjectId = sj.id left join subject_detail sd on sj.detailId = sd.id left join admin a on a.id = sj.professorId
and sj.semester = #{condition.semester}
where sc.studentId = #{ studentId } order by year, semester

mapper.update:
update score

	
		score = #{ score },
	
	
		majorAdmit = #{ majorAdmit },
	
	
		substitutionCode = #{ substitutionCode }
	
 
where id = #{ id };
